package org.example.crudController.crudCtrlImpl;import org.example.crudController.crudCtrl;import org.example.dao.daoImpl.WarehouseDAO;import org.example.entity.Warehouse;import java.util.List;import java.util.Optional;import java.util.Scanner;public class crudLocator implements crudCtrl {    WarehouseDAO warehouseDAO = new WarehouseDAO();    Scanner sc = new Scanner(System.in);    @Override    public void insert() {        try {            Warehouse warehouse = new Warehouse();            warehouseDAO.create(warehouse);        } catch (Exception e) {            System.out.println("Lựa chọn không hợp lệ. Vui lòng chọn lại.");        }    }    @Override    public void delete() {        try {            Scanner sc = new Scanner(System.in);            System.out.println("Enter Warehouse ID");            int id = sc.nextInt();            Optional<Warehouse> warehouse = warehouseDAO.findById(id);            if (warehouse.isPresent()) {                warehouseDAO.delete(id);                System.out.println("Warehouse deleted successfully.");            } else {                System.out.println("Warehouse does not exist.");            }        } catch (Exception e) {            System.out.println("Invalid input. Please try again.");        }    }    @Override    public void update() {    }    @Override    public void read() {        List<Warehouse> list = warehouseDAO.findAll();        for (Warehouse warehouse : list) {            System.out.println(warehouse);        }    }}