package org.example.crudCtrl.crudCtrlImpl;import org.example.dao.daoImpl.ProductDAO;import org.example.dao.daoImpl.ProductItemDAO;import org.example.entity.abstractClass.abstractProduct;import java.util.List;public class crudProduct {    ProductDAO productDAO = new ProductDAO();    public void sortAsc() {        List<abstractProduct> list = productDAO.selectAllAsc();        if (list.size() > 0) {            for (abstractProduct abstractProduct : list) {                System.out.println(abstractProduct);            }        } else {            System.out.println("Không tìm thấy sản phẩm");        }    }    public void sortDesc() {        List<abstractProduct> list = productDAO.selectAllDesc();        if (list.size() > 0) {            for (abstractProduct abstractProduct : list) {                System.out.println(abstractProduct);            }        } else {            System.out.println("Không tìm thấy sản phẩm");        }    }}