package org.example.entity;import org.example.entity.abstractClass.abstractProduct;import org.example.utils.ProductType;import java.sql.Timestamp;public class ProductItem extends abstractProduct {    private ProductType productType;    public ProductItem() {    }    public ProductItem(int id, String name, Boolean isActive, int locatorId, Timestamp created, String createdBy,                       int qtyStock, int productParentId, ProductType productType) {        super(id, name, isActive, locatorId, created, createdBy, qtyStock, productParentId);        this.productType = productType;    }    public ProductType getProductType() {        return productType;    }    public void setProductType(ProductType productType) {        this.productType = productType;    }}