package org.example.entity;import org.example.entity.abstractClass.abstractInfo;import java.sql.Timestamp;import java.util.Scanner;public class Warehouse extends abstractInfo {    private String description;    public Warehouse() {}    public Warehouse(int id, String name, boolean isActive, String description, String createdBy,                     Timestamp created) {        super(id, name, isActive, createdBy, created);        this.description = description;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }    public void input(Scanner sc) {        super.input(sc);        System.out.print("Nhập mô tả: ");        this.description = sc.nextLine();    }}