package org.example.entity.abstractClass;import java.sql.Timestamp;import java.util.Scanner;public abstract class abstractInfo {    private int id;    private String name;    private boolean isActive;    private String createdBy;    private Timestamp created;    public abstractInfo() {    }    public abstractInfo(int id, String name, boolean isActive, String createdBy, Timestamp created) {        this.id = id;        this.name = name;        this.isActive = isActive;        this.createdBy = createdBy;        this.created = created;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public boolean isActive() {        return isActive;    }    public void setActive(boolean active) {        isActive = active;    }    public String getCreatedBy() {        return createdBy;    }    public void setCreatedBy(String createdBy) {        this.createdBy = createdBy;    }    public Timestamp getCreated() {        return created;    }    public void setCreated(Timestamp created) {        this.created = created;    }    public void input(Scanner sc) {        System.out.print("Nhập tên: ");        this.name = sc.nextLine();        System.out.print("Nhập người tạo: ");        this.createdBy = sc.nextLine();        System.out.println("1. Đang hoạt động - 2. Ngừng hoạt động ");        System.out.print("Trạng thái kho: ");        int statusChoice = sc.nextInt();        sc.nextLine();        switch (statusChoice) {            case 1:                this.isActive = true;                break;            case 2:                this.isActive = false;                break;            default:                System.out.println("Lựa chọn không hợp lệ. Đặt trạng thái không thành công.");                this.isActive = false;                break;        }    }}