package org.example.system.menu;import org.example.crudCtrl.crudCtrlImpl.crudLocator;import java.util.Scanner;public class LocatorMenu {    private static Scanner sc = new Scanner(System.in);    private static crudLocator crudLocator = new crudLocator();    public static void displayMenu() {        System.out.println("┌------------- Locator -------------┐");        System.out.println("|1. Xem tất cả thông tin locator    |");        System.out.println("|2. Thêm mới locator                |");        System.out.println("|3. Cập nhật locator                |");        System.out.println("|4. Xóa locator                     |");        System.out.println("|5. Tìm locator                     |");        System.out.println("|6. Sắp xếp locator tăng dần        |");        System.out.println("|7. Sắp xếp locator giảm dần        |");        System.out.println("|0. Quay lại                        |");        System.out.println("└-----------------------------------┘");        System.out.print("Chọn chức năng cần sử dụng: ");    }    public static void runLocator() {        boolean running = true;        while (running) {            displayMenu();            int option = sc.nextInt();            switch (option) {                case 1:                    crudLocator.read();                    break;                case 2:                    crudLocator.insert();                    break;                case 3:                    crudLocator.update();                    break;                case 4:                    crudLocator.delete();                    break;                case 5:                    crudLocator.findByName();                    break;                case 6:                    crudLocator.sort("asc");                    break;                case 7:                    crudLocator.sort("desc");                    break;                case 0:                    running = false;                    break;                default:                    System.out.println("Lựa chọn không hợp lệ. Vui lòng chọn lại.");            }        }    }}